FROM depository_record dr WHERE dr.id IN

    #{id}

WITH MonthlyAmounts AS ( SELECT DATE_FORMAT(time, '%Y-%m') as month, -- Using 'time' column from once_fill depositoryId as depository_id, -- Using 'depositoryId' column from once_fill 1 as type, -- Inbound for once_fill records SUM(unit_price * quantity) as amount FROM once_fill WHERE DATE_FORMAT(time, '%Y') = #{year} GROUP BY DATE_FORMAT(time, '%Y-%m'), depositoryId UNION ALL SELECT DATE_FORMAT(time, '%Y-%m') as month, -- Using 'time' column from once_fill depositoryId as depository_id, -- Using 'depositoryId' column from once_fill 0 as type, -- Outbound for once_fill records SUM(unit_price * quantity) as amount FROM once_fill WHERE DATE_FORMAT(time, '%Y') = #{year} GROUP BY DATE_FORMAT(time, '%Y-%m'), depositoryId
UNION ALL
SELECT DATE_FORMAT(apply_time, '%Y-%m') as month, depository_id, -- Using 'depository_id' column from depository_record type, SUM(price * quantity) as amount FROM depository_record WHERE DATE_FORMAT(apply_time, '%Y') = #{year} AND review_pass = 1 GROUP BY DATE_FORMAT(apply_time, '%Y-%m'), depository_id, type ) SELECT month, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 1 THEN amount ELSE 0 END), 2) as sabInboundAmount, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 0 THEN amount ELSE 0 END), 2) as sabOutboundAmount, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 1 THEN amount ELSE 0 END) - SUM(CASE WHEN depository_id = 1 AND type = 0 THEN amount ELSE 0 END), 2) as sabStockAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 1 THEN amount ELSE 0 END), 2) as zabInboundAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 0 THEN amount ELSE 0 END), 2) as zabOutboundAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 1 THEN amount ELSE 0 END) - SUM(CASE WHEN depository_id = 2 AND type = 0 THEN amount ELSE 0 END), 2) as zabStockAmount FROM MonthlyAmounts GROUP BY month ORDER BY month;

WITH CategoryOutbounds AS ( -- 从 once_fill 表查询 SELECT DATE_FORMAT(time, '%Y-%m') as month, c.title AS category, c.id AS category_id, SUM(o.unit_price * o.quantity) AS amount FROM once_fill o JOIN categories c ON o.apply_remark LIKE CONCAT('%', c.title, '%') AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) WHERE DATE_FORMAT(time, '%Y') = #{year} AND DATE_FORMAT(time, '%m') = #{month} AND o.depositoryId= #{depositoryId} AND c.depository_id= #{depositoryId} GROUP BY DATE_FORMAT(time, '%Y-%m'), c.id, c.title
UNION ALL
-- 从 depository_record 表查询 SELECT DATE_FORMAT(apply_time, '%Y-%m') as month, c.title AS category, c.id AS category_id, SUM(d.price * d.quantity) AS amount FROM depository_record d JOIN categories c ON d.apply_remark LIKE CONCAT('%', c.title, '%') AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) WHERE d.type = 0 AND d.review_pass = 1 AND d.depository_id= #{depositoryId} AND c.depository_id= #{depositoryId} AND DATE_FORMAT(apply_time, '%Y') = #{year} AND DATE_FORMAT(apply_time, '%m') = #{month} GROUP BY DATE_FORMAT(apply_time, '%Y-%m'), c.id, c.title ) SELECT month, category, ROUND(SUM(amount)) AS amount FROM CategoryOutbounds GROUP BY month, category, category_id ORDER BY month, category_id;
