WITH MonthlyAmounts AS ( SELECT DATE_FORMAT(time, '%Y-%m') as month, -- Using 'time' column from once_fill depositoryId as depository_id, -- Using 'depositoryId' column from once_fill 1 as type, -- Inbound for once_fill records SUM(unit_price * quantity) as amount FROM once_fill WHERE DATE_FORMAT(time, '%Y') = #{year} GROUP BY DATE_FORMAT(time, '%Y-%m'), depositoryId UNION ALL SELECT DATE_FORMAT(time, '%Y-%m') as month, -- Using 'time' column from once_fill depositoryId as depository_id, -- Using 'depositoryId' column from once_fill 0 as type, -- Outbound for once_fill records SUM(unit_price * quantity) as amount FROM once_fill WHERE DATE_FORMAT(time, '%Y') = #{year} GROUP BY DATE_FORMAT(time, '%Y-%m'), depositoryId
UNION ALL
SELECT DATE_FORMAT(apply_time, '%Y-%m') as month, depository_id, -- Using 'depository_id' column from depository_record type, SUM(price * quantity) as amount FROM depository_record WHERE DATE_FORMAT(apply_time, '%Y') = #{year} AND review_pass = 1 GROUP BY DATE_FORMAT(apply_time, '%Y-%m'), depository_id, type ) SELECT month, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 1 THEN amount ELSE 0 END), 2) as sabInboundAmount, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 0 THEN amount ELSE 0 END), 2) as sabOutboundAmount, ROUND(SUM(CASE WHEN depository_id = 1 AND type = 1 THEN amount ELSE 0 END) - SUM(CASE WHEN depository_id = 1 AND type = 0 THEN amount ELSE 0 END), 2) as sabStockAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 1 THEN amount ELSE 0 END), 2) as zabInboundAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 0 THEN amount ELSE 0 END), 2) as zabOutboundAmount, ROUND(SUM(CASE WHEN depository_id = 2 AND type = 1 THEN amount ELSE 0 END) - SUM(CASE WHEN depository_id = 2 AND type = 0 THEN amount ELSE 0 END), 2) as zabStockAmount FROM MonthlyAmounts GROUP BY month ORDER BY month;

-- 创建一个包含所有月份的临时表 WITH AllMonths AS ( SELECT CONCAT(#{year}, '-01') AS month UNION ALL SELECT CONCAT(#{year}, '-02') UNION ALL SELECT CONCAT(#{year}, '-03') UNION ALL SELECT CONCAT(#{year}, '-04') UNION ALL SELECT CONCAT(#{year}, '-05') UNION ALL SELECT CONCAT(#{year}, '-06') UNION ALL SELECT CONCAT(#{year}, '-07') UNION ALL SELECT CONCAT(#{year}, '-08') UNION ALL SELECT CONCAT(#{year}, '-09') UNION ALL SELECT CONCAT(#{year}, '-10') UNION ALL SELECT CONCAT(#{year}, '-11') UNION ALL SELECT CONCAT(#{year}, '-12') ), CategoryOutbounds AS ( -- 从 once_fill 表查询 SELECT DATE_FORMAT(time, '%Y-%m') as month, c.title AS category, c.id AS category_id, SUM(o.unit_price * o.quantity) AS amount FROM once_fill o JOIN categories c ON CASE WHEN LOCATE('-', o.apply_remark) > 0 THEN RIGHT(SUBSTRING_INDEX(o.apply_remark, '-', -1), LENGTH(c.title)) = c.title ELSE o.apply_remark LIKE CONCAT('%', c.title, '%') END AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) AND c.parent_id NOT IN (SELECT id FROM categories WHERE title = '销售出库') WHERE DATE_FORMAT(time, '%Y') = #{year} AND o.depositoryId = #{depositoryId} AND c.depository_id = #{depositoryId} AND c.title = #{categoryTitle} AND o.introduce !='立项' GROUP BY DATE_FORMAT(time, '%Y-%m'), c.id, c.title
UNION ALL
-- 从 depository_record 表查询 SELECT DATE_FORMAT(apply_time, '%Y-%m') as month, c.title AS category, c.id AS category_id, SUM(d.price * d.quantity) AS amount FROM depository_record d JOIN categories c ON CASE WHEN LOCATE('-', d.apply_remark) > 0 THEN RIGHT(SUBSTRING_INDEX(d.apply_remark, '-', -1), LENGTH(c.title)) = c.title ELSE d.apply_remark LIKE CONCAT('%', c.title, '%') END AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) AND c.parent_id NOT IN (SELECT id FROM categories WHERE title = '销售出库') WHERE d.type = 0 AND d.review_pass = 1 AND d.depository_id = #{depositoryId} AND c.depository_id = #{depositoryId} AND DATE_FORMAT(apply_time, '%Y') = #{year} AND c.title = #{categoryTitle} GROUP BY DATE_FORMAT(apply_time, '%Y-%m'), c.id, c.title ) SELECT a.month, coalesce(co.category, #{categoryTitle}) AS category, coalesce(ROUND(SUM(co.amount)), 0) AS amount FROM AllMonths a LEFT JOIN CategoryOutbounds co ON a.month = co.month GROUP BY a.month, co.category, co.category_id ORDER BY a.month, co.category_id;

-- 创建一个包含所有月份的临时表 WITH AllMonths AS ( SELECT CONCAT(#{year}, '-01') AS month UNION ALL SELECT CONCAT(#{year}, '-02') UNION ALL SELECT CONCAT(#{year}, '-03') UNION ALL SELECT CONCAT(#{year}, '-04') UNION ALL SELECT CONCAT(#{year}, '-05') UNION ALL SELECT CONCAT(#{year}, '-06') UNION ALL SELECT CONCAT(#{year}, '-07') UNION ALL SELECT CONCAT(#{year}, '-08') UNION ALL SELECT CONCAT(#{year}, '-09') UNION ALL SELECT CONCAT(#{year}, '-10') UNION ALL SELECT CONCAT(#{year}, '-11') UNION ALL SELECT CONCAT(#{year}, '-12') ), CategoryOutbounds AS ( -- 从 once_fill 表查询 SELECT DATE_FORMAT(time, '%Y-%m') AS month, SUM(o.unit_price * o.quantity) AS amount FROM once_fill o JOIN categories c ON CASE WHEN LOCATE('-', o.apply_remark) > 0 THEN RIGHT(SUBSTRING_INDEX(o.apply_remark, '-', -1), LENGTH(c.title)) = c.title ELSE o.apply_remark LIKE CONCAT('%', c.title, '%') END AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) AND c.parent_id NOT IN (SELECT id FROM categories WHERE title = '销售出库') WHERE DATE_FORMAT(time, '%Y') = #{year} AND o.depositoryId = #{depositoryId} AND c.depository_id = #{depositoryId} AND o.introduce !='立项' GROUP BY DATE_FORMAT(time, '%Y-%m')
UNION ALL
-- 从 depository_record 表查询 SELECT DATE_FORMAT(apply_time, '%Y-%m') AS month, SUM(d.price * d.quantity) AS amount FROM depository_record d JOIN categories c ON CASE WHEN LOCATE('-', d.apply_remark) > 0 THEN RIGHT(SUBSTRING_INDEX(d.apply_remark, '-', -1), LENGTH(c.title)) = c.title ELSE d.apply_remark LIKE CONCAT('%', c.title, '%') END AND c.id NOT IN (SELECT parent_id FROM categories WHERE parent_id IS NOT NULL) AND c.parent_id NOT IN (SELECT id FROM categories WHERE title = '销售出库') WHERE d.type = 0 AND d.review_pass = 1 AND d.depository_id = #{depositoryId} AND c.depository_id = #{depositoryId} AND DATE_FORMAT(apply_time, '%Y') = #{year} GROUP BY DATE_FORMAT(apply_time, '%Y-%m') ) SELECT a.month, COALESCE(ROUND(SUM(co.amount)), 0) AS amount FROM AllMonths a LEFT JOIN CategoryOutbounds co ON a.month = co.month GROUP BY a.month ORDER BY a.month;

WITH MonthlyAmount AS ( SELECT MONTH(combined.date) AS month, ROUND(SUM(combined.total_price), 2) AS monthly_amount FROM ( -- 从 depository_record 表查询金额 SELECT dr.type_name AS type_name, dr.apply_time AS date, dr.quantity * dr.price AS total_price FROM depository_record dr WHERE dr.type_name IN (SELECT tname FROM material_type WHERE id = #{typeId}) AND YEAR(dr.apply_time) = #{year} and depository_id =#{depositoryId}
UNION ALL
-- 从 once_fill 表查询金额 SELECT mt.tname AS type_name, off.time AS date, off.unit_price * off.quantity AS total_price FROM once_fill off INNER JOIN material_type mt ON off.type_id = mt.id WHERE mt.id = #{typeId} AND YEAR(off.time) = #{year} and depositoryId =#{depositoryId} ) AS combined GROUP BY MONTH(combined.date) ), TypeClassification AS ( SELECT id AS typeId, CASE WHEN tname LIKE '%小径型号%' THEN 'smallDiameter' WHEN tname LIKE '%中径型号%' THEN 'mediumDiameter' WHEN tname LIKE '%万向节%' THEN 'gimbal' WHEN tname LIKE '%RAB%' THEN 'rab' -- 如果有其他分类，可以继续在此添加 ELSE 'other' END AS classification FROM material_type ), MonthlyProduction AS ( SELECT MONTH(date) as month, CASE WHEN tc.classification = 'smallDiameter' THEN smallDiameter WHEN tc.classification = 'mediumDiameter' THEN mediumDiameter WHEN tc.classification = 'gimbal' THEN gimbal WHEN tc.classification = 'rab' THEN rab WHEN tc.classification = 'other' THEN (smallDiameter + mediumDiameter + gimbal + rab) ELSE 0 END as production FROM productinfo JOIN TypeClassification tc ON tc.typeId = #{typeId} WHERE YEAR(date) = #{year} AND depositoryId = #{depositoryId} ) SELECT a.month, a.monthly_amount, p.production, ROUND(CASE WHEN p.production = 0 THEN 0 ELSE a.monthly_amount / p.production END, 6) AS unit_price FROM MonthlyAmount a JOIN MonthlyProduction p ON a.month = p.month ORDER BY a.month;
